import React, { useMemo, useState } from "react";
import {
  useReactTable,
  getCoreRowModel,
  getFilteredRowModel,
  getSortedRowModel,
  flexRender,
  ColumnDef,
  ColumnFiltersState,
} from "@tanstack/react-table";
import { Tax, Country, EditTaxData } from "../../types/types";
import { EditModal } from "../EditModal/EditModal";
import "./TaxTable.css";

interface TaxTableProps {
  taxes: Tax[];
  countries: Country[];
  onUpdateTax: (id: string, data: EditTaxData) => Promise<boolean>;
  loading?: boolean;
}

export const TaxTable: React.FC<TaxTableProps> = ({
  taxes,
  countries,
  onUpdateTax,
  loading = false,
}) => {
  const [editingTax, setEditingTax] = useState<Tax | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);
  const [showCountryFilter, setShowCountryFilter] = useState(false);

  const handleEdit = (tax: Tax) => {
    setEditingTax(tax);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setEditingTax(null);
    setIsModalOpen(false);
  };

  const handleSave = async (data: EditTaxData): Promise<boolean> => {
    if (!editingTax) return false;
    return await onUpdateTax(editingTax.id, data);
  };

  const getEditInitialData = (): EditTaxData => {
    if (!editingTax) return { name: "", country: "" };
    return {
      name: editingTax.name || "",
      country: editingTax.country || "",
    };
  };

  const toggleCountryFilter = () => {
    setShowCountryFilter((prev) => !prev);
  };

  const handleCountryFilterChange = (country: string) => {
    table.getColumn("country")?.setFilterValue(country);
    setShowCountryFilter(false);
  };

  // --------------------------
  // TABLE COLUMNS
  // --------------------------
  const columns = useMemo<ColumnDef<Tax>[]>(
    () => [
      {
        header: "Entity",
        accessorKey: "name",
        cell: (info) => (
          <span className="entity-cell">
            {info.getValue() as string}
          </span>
        ),
      },
      {
        header: "Gender",
        accessorKey: "gender",
        cell: (info) => {
          const gender = info.getValue() as string;
          const genderLower = gender.toLowerCase();
          let genderClass = 'gender-other';
          
          if (genderLower.includes('male')) {
            genderClass = 'gender-male';
          } else if (genderLower.includes('female')) {
            genderClass = 'gender-female';
          }
          
          const displayGender = gender.charAt(0).toUpperCase() + gender.slice(1).toLowerCase();
          
          return (
            <span className={genderClass}>
              {displayGender}
            </span>
          );
        },
      },
      {
        header: "Request date",
        accessorKey: "createdAt",
        cell: (info) => {
          const dateString = info.getValue() as string;
          try {
            return new Date(dateString).toLocaleDateString("en-US", {
              month: "short",
              day: "numeric",
              year: "numeric",
            });
          } catch {
            return "Invalid Date";
          }
        },
      },
      {
        header: "Country",
        accessorKey: "country",
        cell: (info) => (
          <span className="country-cell">
            {info.getValue() as string}
          </span>
        ),
      },
      {
        // ✅ FILTER ICON IN HEADER INSTEAD OF "ACTIONS"
        header: () => (
          <div className="filter-header-column">
            <button
              onClick={toggleCountryFilter}
              className="filter-header-btn"
              title="Filter by country"
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path d="M22 3H2l8 9.46V19l4 2v-8.54L22 3z"/>
              </svg>
            </button>
          </div>
        ),
        accessorKey: "id",
        cell: (info) => (
          <button
            onClick={() => handleEdit(info.row.original)}
            className="edit-btn"
            title="Edit"
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
              <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"/>
              <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"/>
            </svg>
          </button>
        ),
      },
    ],
    []
  );

  const table = useReactTable({
    data: taxes,
    columns,
    state: {
      columnFilters,
    },
    onColumnFiltersChange: setColumnFilters,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
  });

  if (loading) return <div className="table-loading">Loading taxes...</div>;

  return (
    <div className="tax-table-container">
      {/* ✅ MAIN HEADER */}
      <div className="table-main-header">
        <h3>Tax Records</h3>
        <div className="results-info">
          Showing {table.getRowModel().rows.length} of {taxes.length} records
          {(table.getColumn("country")?.getFilterValue() as string) && (
            <span className="filter-active">
              • Filtered by: {(table.getColumn("country")?.getFilterValue() as string)}
            </span>
          )}
        </div>
      </div>

      {/* ✅ FILTER DROPDOWN (Positioned relative to the filter icon) */}
      {showCountryFilter && (
        <div className="country-filter-dropdown-header">
          <div className="filter-header">
            <span>Filter by Country</span>
            <button 
              onClick={() => setShowCountryFilter(false)}
              className="close-filter"
            >
              ×
            </button>
          </div>
          
          <label className="filter-option">
            <input
              type="radio"
              name="country"
              value=""
              checked={!table.getColumn("country")?.getFilterValue()}
              onChange={() => handleCountryFilterChange("")}
            />
            <span>All Countries</span>
          </label>

          {Array.from(new Set(taxes.map((t) => t.country))).sort().map((country) => (
            <label key={country} className="filter-option">
              <input
                type="radio"
                name="country"
                value={country}
                checked={table.getColumn("country")?.getFilterValue() === country}
                onChange={() => handleCountryFilterChange(country)}
              />
              <span>{country}</span>
            </label>
          ))}
        </div>
      )}

      {/* TABLE */}
      <table className="tax-table">
        <thead>
          {table.getHeaderGroups().map((hg) => (
            <tr key={hg.id}>
              {hg.headers.map((header) => (
                <th key={header.id}>
                  {flexRender(header.column.columnDef.header, header.getContext())}
                </th>
              ))}
            </tr>
          ))}
        </thead>

        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id}>
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {table.getRowModel().rows.length === 0 && !loading && (
        <div className="no-data">
          No records found
          {(table.getColumn("country")?.getFilterValue() as string) && (
            <span> for country "{(table.getColumn("country")?.getFilterValue() as string)}"</span>
          )}
        </div>
      )}

      {/* EDIT MODAL */}
      {isModalOpen && (
        <EditModal
          isOpen={isModalOpen}
          onClose={handleCloseModal}
          onSave={handleSave}
          initialData={getEditInitialData()}
          countries={countries}
        />
      )}
    </div>
  );
};
