import { useState, useEffect } from 'react';
import { Tax, Country } from '../types/types';
import { taxesAPI, countriesAPI } from '../services/api';

export const useTaxData = () => {
  const [taxes, setTaxes] = useState<Tax[]>([]);
  const [countries, setCountries] = useState<Country[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchTaxes = async () => {
    try {
      setLoading(true);
      const response = await taxesAPI.getAll();
      setTaxes(response.data);
    } catch (err) {
      setError('Failed to fetch taxes');
    } finally {
      setLoading(false);
    }
  };

  const fetchCountries = async () => {
    try {
      const response = await countriesAPI.getAll();
      setCountries(response.data);
    } catch (err) {
      setError('Failed to fetch countries');
    }
  };

  const updateTax = async (id: string, data: any) => {
    try {
      await taxesAPI.update(id, data);
      await fetchTaxes(); // Refresh data
      return true;
    } catch (err) {
      setError('Failed to update tax');
      return false;
    }
  };

  useEffect(() => {
    const loadData = async () => {
      await Promise.all([fetchTaxes(), fetchCountries()]);
    };
    loadData();
  }, []);

  return {
    taxes,
    countries,
    loading,
    error,
    updateTax,
    refetchTaxes: fetchTaxes,
  };
};
