const columns = useMemo<ColumnDef<Tax>[]>(
  () => [
    {
      header: "Entity",
      accessorKey: "name",
      cell: (info) => (
        <a href="#" onClick={(e) => e.preventDefault()}>
          {info.getValue() as string}
        </a>
      ),
    },
    {
      header: "Gender",
      accessorKey: "gender",
      cell: (info) => info.getValue() as string,
    },
    {
      header: "Request date",
      accessorKey: "createdAt",
      cell: (info) => {
        const dateString = info.getValue() as string;
        return new Date(dateString).toLocaleDateString("en-US", {
          month: "short",
          day: "numeric",
          year: "numeric",
        });
      },
    },

    // -----------------------------
    // COUNTRY FILTER COLUMN
    // -----------------------------
    {
      header: () => (
        <div style={{ display: "flex", alignItems: "center", gap: "6px" }}>
          <span>Country</span>

          {/* Filter icon */}
          <button
            onClick={toggleCountryFilter}
            style={{
              background: "transparent",
              border: "none",
              cursor: "pointer",
            }}
          >
            üîç
          </button>

          {/* Dropdown */}
          {showCountryFilter && (
            <div
              style={{
                position: "absolute",
                marginTop: "30px",
                background: "white",
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "6px",
                zIndex: 10,
              }}
            >
              <label>
                <input
                  type="radio"
                  name="country"
                  value=""
                  checked={!table.getColumn("country")?.getFilterValue()}
                  onChange={() => handleCountryFilterChange("")}
                />
                All Countries
              </label>

              {Array.from(new Set(taxes.map((t) => t.country))).map((c) => (
                <label key={c} style={{ display: "block" }}>
                  <input
                    type="radio"
                    name="country"
                    value={c}
                    checked={table.getColumn("country")?.getFilterValue() === c}
                    onChange={() => handleCountryFilterChange(c)}
                  />
                  {c}
                </label>
              ))}
            </div>
          )}
        </div>
      ),
      accessorKey: "country",
      cell: (info) => info.getValue() as string,
    },

    // -----------------------------
    // REPLACE ACTIONS WITH FILTER ICON COLUMN
    // -----------------------------
    {
      header: () => (
        <button
          onClick={toggleCountryFilter}
          style={{
            background: "transparent",
            border: "none",
            cursor: "pointer",
            fontSize: "18px",
          }}
        >
          üîç
        </button>
      ),
      id: "actions-filter",
      cell: () => null,
    },
  ],
  [taxes, showCountryFilter]
);
