import React, { useMemo, useState } from 'react';
import {
  useReactTable,
  getCoreRowModel,
  getFilteredRowModel,
  getSortedRowModel,
  flexRender,
  ColumnDef,
} from '@tanstack/react-table';
import { Tax, Country, EditTaxData } from '../../types/types';
import { EditModal } from '../EditModal/EditModal';
import './TaxTable.css';

interface TaxTableProps {
  taxes: Tax[];
  countries: Country[];
  onUpdateTax: (id: string, data: EditTaxData) => Promise<boolean>;
  loading?: boolean;
}

export const TaxTable: React.FC<TaxTableProps> = ({
  taxes,
  countries,
  onUpdateTax,
  loading = false,
}) => {
  const [editingTax, setEditingTax] = useState<Tax | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const columns = useMemo<ColumnDef<Tax>[]>(
    () => [
      {
        header: 'Entity',
        accessorKey: 'name',  // Using 'name' from API for Entity
        cell: (info) => info.getValue(),
      },
      {
        header: 'Gender',
        accessorKey: 'gender',
        cell: (info) => {
          const gender = info.getValue() as string;
          return gender.charAt(0).toUpperCase() + gender.slice(1).toLowerCase();
        },
      },
      {
        header: 'Percent dent',
        accessorKey: 'createdAt',  // Using 'createdAt' for date display
        cell: (info) => {
          const dateString = info.getValue() as string;
          // Format date like "Jan 30, 2025"
          try {
            return new Date(dateString).toLocaleDateString('en-US', {
              month: 'short',
              day: 'numeric',
              year: 'numeric'
            });
          } catch {
            return 'Invalid Date';
          }
        },
      },
      {
        header: 'Country',
        accessorKey: 'country',
        cell: (info) => info.getValue(),
      },
      {
        header: 'Y',
        accessorKey: 'id',
        cell: (info) => (
          <button
            onClick={() => handleEdit(info.row.original)}
            className="edit-btn"
            title="Edit"
          >
            ✏️ {/* Pencil icon for edit */}
          </button>
        ),
      },
    ],
    []
  );

  const table = useReactTable({
    data: taxes,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
  });

  const handleEdit = (tax: Tax) => {
    setEditingTax(tax);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingTax(null);
  };

  const handleSave = async (data: EditTaxData): Promise<boolean> => {
    if (!editingTax) return false;
    return await onUpdateTax(editingTax.id, data);
  };

  const getEditInitialData = (): EditTaxData => {
    if (!editingTax) return { name: '', country: '' };
    
    return {
      name: editingTax.name || '',      // Using 'name' from API
      country: editingTax.country || '',
    };
  };

  if (loading) {
    return <div className="table-loading">Loading taxes...</div>;
  }

  return (
    <div className="tax-table-container">
      <table className="tax-table">
        <thead>
          {table.getHeaderGroups().map((headerGroup) => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map((header) => (
                <th key={header.id}>
                  {flexRender(
                    header.column.columnDef.header,
                    header.getContext()
                  )}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id}>
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {taxes.length === 0 && !loading && (
        <div className="no-data">No tax records found</div>
      )}

      <EditModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        onSave={handleSave}
        initialData={getEditInitialData()}
        countries={countries}
      />
    </div>
  );
};
