import React, { useMemo, useState } from 'react';
import {
  useReactTable,
  getCoreRowModel,
  getFilteredRowModel,
  getSortedRowModel,
  flexRender,
  ColumnDef,
  ColumnFiltersState,
} from '@tanstack/react-table';
import { Tax, Country, EditTaxData } from '../../types/types';
import { EditModal } from '../EditModal/EditModal';
import './TaxTable.css';

interface TaxTableProps {
  taxes: Tax[];
  countries: Country[];
  onUpdateTax: (id: string, data: EditTaxData) => Promise<boolean>;
  loading?: boolean;
}

export const TaxTable: React.FC<TaxTableProps> = ({
  taxes,
  countries,
  onUpdateTax,
  loading = false,
}) => {
  const [editingTax, setEditingTax] = useState<Tax | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);

  console.log('Modal state:', isModalOpen);

  // ✅ MOVE FUNCTIONS UP - DEFINED BEFORE COLUMNS
  const handleEdit = (tax: Tax) => {
    console.log('Setting editing tax:', tax.name);
    setEditingTax(tax);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    console.log('Closing modal');
    setIsModalOpen(false);
    setEditingTax(null);
  };

  const handleSave = async (data: EditTaxData): Promise<boolean> => {
    console.log('Saving data:', data);
    if (!editingTax) return false;
    return await onUpdateTax(editingTax.id, data);
  };

  const getEditInitialData = (): EditTaxData => {
    if (!editingTax) return { name: '', country: '' };
    
    return {
      name: editingTax.name || '',
      country: editingTax.country || '',
    };
  };

  const columns = useMemo<ColumnDef<Tax>[]>(
    () => [
      {
        header: 'Entity',
        accessorKey: 'name',
        cell: (info) => (
          <span className="entity-cell">
            {info.getValue() as string}
          </span>
        ),
      },
      {
        header: 'Gender',
        accessorKey: 'gender',
        cell: (info) => {
          const gender = info.getValue() as string;
          const genderLower = gender.toLowerCase();
          let genderClass = 'gender-other';
          
          if (genderLower === 'male' || genderLower === 'male') {
            genderClass = 'gender-male';
          } else if (genderLower === 'female' || genderLower === 'female') {
            genderClass = 'gender-female';
          }
          
          const displayGender = gender.charAt(0).toUpperCase() + gender.slice(1).toLowerCase();
          
          return (
            <span className={genderClass}>
              {displayGender}
            </span>
          );
        },
      },
      {
        header: 'Percent dent',
        accessorKey: 'createdAt',
        cell: (info) => {
          const dateString = info.getValue() as string;
          try {
            const formattedDate = new Date(dateString).toLocaleDateString('en-US', {
              month: 'short',
              day: 'numeric',
              year: 'numeric'
            });
            return <span className="date-cell">{formattedDate}</span>;
          } catch {
            return <span className="date-cell">Invalid Date</span>;
          }
        },
      },
      {
        header: 'Country',
        accessorKey: 'country',
        cell: (info) => (
          <span className="country-cell">
            {info.getValue() as string}
          </span>
        ),
      },
      {
        header: 'Y',
        accessorKey: 'id',
        cell: (info) => (
          <button
            onClick={() => {
              console.log('Edit clicked for:', info.row.original.name);
              handleEdit(info.row.original);
            }}
            className="edit-btn"
            title="Edit"
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
        <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"/>
        <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"/>
        </svg>
          </button>
        ),
      },
    ],
    []
  );

  const table = useReactTable({
    data: taxes,
    columns,
    state: {
      columnFilters,
    },
    onColumnFiltersChange: setColumnFilters,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
  });

  if (loading) {
    return <div className="table-loading">Loading taxes...</div>;
  }

  return (
    <div className="tax-table-container">
      {/* ✅ ADD FILTERS SECTION */}
      <div className="filters-section">
        <h3>Filters</h3>
        <div className="filter-group">
          <label htmlFor="country-filter">Country:</label>
          <select
            id="country-filter"
            value={(table.getColumn('country')?.getFilterValue() as string) ?? ''}
            onChange={(event) =>
              table.getColumn('country')?.setFilterValue(event.target.value)
            }
            className="filter-select"
          >
            <option value="">All Countries</option>
            {Array.from(new Set(taxes.map(tax => tax.country))).map((country) => (
              <option key={country} value={country}>
                {country}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="gender-filter">Gender:</label>
          <select
            id="gender-filter"
            value={(table.getColumn('gender')?.getFilterValue() as string) ?? ''}
            onChange={(event) =>
              table.getColumn('gender')?.setFilterValue(event.target.value)
            }
            className="filter-select"
          >
            <option value="">All Genders</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="search-filter">Search:</label>
          <input
            id="search-filter"
            type="text"
            placeholder="Search by name..."
            value={(table.getColumn('name')?.getFilterValue() as string) ?? ''}
            onChange={(event) =>
              table.getColumn('name')?.setFilterValue(event.target.value)
            }
            className="filter-input"
          />
        </div>

        {/* Clear Filters Button */}
        <button
          onClick={() => setColumnFilters([])}
          className="clear-filters-btn"
        >
          Clear Filters
        </button>
      </div>

      {/* Results Count */}
      <div className="results-info">
        Showing {table.getRowModel().rows.length} of {taxes.length} records
      </div>

      <table className="tax-table">
        <thead>
          {table.getHeaderGroups().map((headerGroup) => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map((header) => (
                <th key={header.id}>
                  {flexRender(
                    header.column.columnDef.header,
                    header.getContext()
                  )}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id}>
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {table.getRowModel().rows.length === 0 && !loading && (
        <div className="no-data">No records found matching your filters</div>
      )}

      {/* Edit Modal */}
      {isModalOpen && (
        <EditModal
          isOpen={isModalOpen}
          onClose={handleCloseModal}
          onSave={handleSave}
          initialData={getEditInitialData()}
          countries={countries}
        />
      )}
    </div>
  );
};
