import React, { useMemo, useState } from "react";
import {
  useReactTable,
  getCoreRowModel,
  getFilteredRowModel,
  getSortedRowModel,
  flexRender,
  ColumnDef,
  ColumnFiltersState,
} from "@tanstack/react-table";

import { Tax, Country, EditTaxData } from "../../types/types";
import { EditModal } from "../EditModal/EditModal";

interface TaxTableProps {
  taxes: Tax[];
  countries: Country[];
  onUpdateTax: (id: string, data: EditTaxData) => Promise<boolean>;
  loading?: boolean;
}

export const TaxTable: React.FC<TaxTableProps> = ({
  taxes,
  countries,
  onUpdateTax,
  loading = false,
}) => {
  const [editingTax, setEditingTax] = useState<Tax | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);
  const [showCountryFilter, setShowCountryFilter] = useState(false);

  const handleEdit = (tax: Tax) => {
    setEditingTax(tax);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setEditingTax(null);
    setIsModalOpen(false);
  };

  const handleSave = async (data: EditTaxData): Promise<boolean> => {
    if (!editingTax) return false;
    return await onUpdateTax(editingTax.id, data);
  };

  const getEditInitialData = (): EditTaxData => {
    if (!editingTax) return { name: "", country: "" };
    return {
      name: editingTax.name || "",
      country: editingTax.country || "",
    };
  };

  // Toggle country dropdown
  const toggleCountryFilter = () => {
    setShowCountryFilter((prev) => !prev);
  };

  const handleCountryFilterChange = (country: string) => {
    table.getColumn("country")?.setFilterValue(country);
    setShowCountryFilter(false);
  };

  // --------------------------
  // TABLE COLUMNS
  // --------------------------
  const columns = useMemo<ColumnDef<Tax>[]>(
    () => [
      {
        header: "Entity",
        accessorKey: "name",
        cell: (info) => (
          <a href="#" onClick={(e) => e.preventDefault()}>
            {info.getValue() as string}
          </a>
        ),
      },
      {
        header: "Gender",
        accessorKey: "gender",
        cell: (info) => info.getValue() as string,
      },
      {
        header: "Request date",
        accessorKey: "createdAt",
        cell: (info) => {
          const dateString = info.getValue() as string;
          return new Date(dateString).toLocaleDateString("en-US", {
            month: "short",
            day: "numeric",
            year: "numeric",
          });
        },
      },

      // COUNTRY COLUMN + DROPDOWN
      {
        header: () => (
          <div style={{ display: "flex", alignItems: "center", gap: "6px", position: "relative" }}>
            <span>Country</span>

            {/* FILTER ICON */}
            <button
              onClick={toggleCountryFilter}
              style={{
                background: "transparent",
                border: "none",
                cursor: "pointer",
                fontSize: "18px",
              }}
            >
              üîç
            </button>

            {/* DROPDOWN */}
            {showCountryFilter && (
              <div
                style={{
                  position: "absolute",
                  top: "24px",
                  left: "0",
                  background: "white",
                  border: "1px solid #ccc",
                  padding: "10px",
                  borderRadius: "6px",
                  zIndex: 100,
                }}
              >
                <label>
                  <input
                    type="radio"
                    name="country"
                    value=""
                    checked={!table.getColumn("country")?.getFilterValue()}
                    onChange={() => handleCountryFilterChange("")}
                  />
                  All Countries
                </label>

                {Array.from(new Set(taxes.map((t) => t.country))).map((c) => (
                  <label key={c} style={{ display: "block" }}>
                    <input
                      type="radio"
                      name="country"
                      value={c}
                      checked={table.getColumn("country")?.getFilterValue() === c}
                      onChange={() => handleCountryFilterChange(c)}
                    />
                    {c}
                  </label>
                ))}
              </div>
            )}
          </div>
        ),
        accessorKey: "country",
        cell: (info) => info.getValue() as string,
      },

      // REPLACING ACTION COLUMN WITH FILTER ICON
      {
        header: () => (
          <button
            onClick={toggleCountryFilter}
            style={{
              background: "transparent",
              border: "none",
              cursor: "pointer",
              fontSize: "18px",
            }}
          >
            üîç
          </button>
        ),
        id: "actions-filter",
        cell: () => null,
      },
    ],
    [taxes, showCountryFilter]
  );

  const table = useReactTable({
    data: taxes,
    columns,
    state: {
      columnFilters,
    },
    onColumnFiltersChange: setColumnFilters,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
  });

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h3>Tax Records</h3>

      {/* TABLE */}
      <table>
        <thead>
          {table.getHeaderGroups().map((hg) => (
            <tr key={hg.id}>
              {hg.headers.map((header) => (
                <th key={header.id}>
                  {flexRender(header.column.columnDef.header, header.getContext())}
                </th>
              ))}
            </tr>
          ))}
        </thead>

        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id}>
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* EDIT MODAL */}
      {isModalOpen && (
        <EditModal
          isOpen={isModalOpen}
          onClose={handleCloseModal}
          onSave={handleSave}
          initialData={getEditInitialData()}
          countries={countries}
        />
      )}
    </div>
  );
};
